use 5.014;
use warnings;

#Code to compute Hamming distance between 2 sequences
#
#ie no. of nucleotides which differ b/w 2 sequences

my $dna1 = '';#'GAGCCTACTAACGGGAT';#'atct';
my $len = length $dna1;
my $dna2 = '';#'CATCGTAATGACGGCCT';#'atcg';

my @dna1 = split '',$dna1;
my @dna2 = split '',$dna2;

# say $dna1[0];#@dna1;
# say $dna2[0];#@dna2;
say $len;

my $i = 0;
my $count = 0;
while ($i <= ($len-1)){
	if ($dna1[$i] ne $dna2[$i]){
		$count++;
	}
	$i++;
}

open OUT, ">Hamming_distance.out" or die $!;
say OUT $count;
#Exit
exit;